import React, { useState } from "react"
import "bootstrap/dist/css/bootstrap.min.css"
import "./auth.css"
import { useFormik } from 'formik';
export default function Auth(props) {
  const [authMode, setAuthMode] = useState("signin")

  const changeAuthMode = () => {
    setAuthMode(authMode === "signin" ? "signup" : "signin")
  }
  const validate = values => {
    const errors = {};
    if (!values.name) {
      errors.firstName = 'Required';
    } else if (values.firstName.length > 15) {
      errors.firstName = 'Must be 15 characters or less';
    }


    if (!values.email) {
      errors.email = 'Required';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Invalid email address';
    }

    if(!values.pass){
      errors.pass = 'Required';
    }else if(values.pass.length <7){
      errors.firstName = 'Must be greater than 7';
    }



    if (!values.SignUpemail) {
      errors.SignUpemail = 'Required';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.SignUppass)) {
      errors.SignUpemail = 'Invalid email address';
    }

    if(!values.SignUppass){
      errors.SignUppass = 'Required';
    }else if(values.SignUppass.length <7){
      errors.SignUppass = 'Must be greater than 7';
    }

    return errors;
  };



  // using formik on form
    const formik = useFormik({
     initialValues: {
       name:'',
       email: '',
       pass:'',
       SignUpemail:'',
       SignUppass:''

     },
     validate,
    })




  if (authMode === "signin") {
    return (
      <div className="Auth-form-container">
        <form className="Auth-form">
          <div className="Auth-form-content">
            <h3 className="Auth-form-title">Sign In</h3>
            <div className="text-center">
              Not registered yet?{" "}
              <span className="link-primary" onClick={changeAuthMode}>
                Sign Up
              </span>
            </div>
            <div className="form-group mt-3">
              <label>Email address</label>
              <input
                type="email"
                id="email"
                name="email"
                className="form-control mt-1"
                placeholder="Enter email"
                onChange={formik.handleChange}
                value={formik.values.email}
                onBlur={formik.handleBlur}
              />
              {formik.touched.email&&formik.errors.email ? <div style={{color:'red'}}>{formik.errors.email}</div> : null}
            </div>
            <div className="form-group mt-3">
              <label>Password</label>
              <input
                 id="pass"
                type="password"
                name="pass"
                className="form-control mt-1"
                placeholder="Enter password"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.pass}
              />
              {formik.touched.pass&&formik.errors.pass ? <div style={{color:'red'}}>{formik.errors.pass}</div> : null}
            </div>
            <div className="d-grid gap-2 mt-3">
              <button type="submit" onSubmit={formik.handleSubmit} className="btn btn-primary">
                Submit
              </button>
            </div>
            <p className="text-center mt-2">
              Forgot <a href="#">password?</a>
            </p>
          </div>
        </form>
      </div>
    )
  }

  return (
    <div className="Auth-form-container">
      <form className="Auth-form">
        <div className="Auth-form-content">
          <h3 className="Auth-form-title">Sign In</h3>
          <div className="text-center">
            Already registered?{" "}
            <span className="link-primary" onClick={changeAuthMode}>
              Sign In
            </span>
          </div>
          <div className="form-group mt-3">
            <label>Full Name</label>
            <input
              type="text"
              id="name"
              name="name"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              value={formik.values.name}
              className="form-control mt-1"
              placeholder="e.g Jane Doe"
            />
            {formik.touched.name&&formik.errors.name ? <div style={{color:'red'}}>{formik.errors.name}</div> : null}
          </div>
          <div className="form-group mt-3">
            <label>Email address</label>
            <input
              type="email"
              id="SignUpemail"
              name="SignUpemail"
              className="form-control mt-1"
              placeholder="Email Address"
              onBlur={formik.handleBlur}
              onChange={formik.handleChange}
              value={formik.values.SignUpemail}
            />
            {formik.touched.SignUpemail&&formik.errors.SignUpemail ? <div style={{color:'red'}}>{formik.errors.SignUpemail}</div> :null}
          </div>
          <div className="form-group mt-3">
            <label>Password</label>
            <input
              type="password"
              id="SignUppass"
              name="SignUppass"
              onChange={formik.handleChange}
              value={formik.values.SignUppass}
              onBlur={formik.handleBlur}
              className="form-control mt-1"
              placeholder="Password"
            />
            {formik.touched.SignUppass&&formik.errors.SignUppass ? <div style={{color:'red'}}>{formik.errors.SignUppass}</div>:null}
          </div>
          <div className="d-grid gap-2 mt-3">
            <button type="submit" onClick={formik.handleSubmit} className="btn btn-primary">
              Submit
            </button>
          </div>
          <p className="text-center mt-2">
            Forgot <a href="#">password?</a>
          </p>
        </div>
      </form>
    </div>
  )
}
