import "bootstrap/dist/css/bootstrap.min.css"
import React, { useState,useEffect } from "react"
import '../LoginAndSignUp/auth.css'
import {Navigate, useNavigate} from 'react-router-dom';
import {CallToServer} from '../backendApiCalls'
import {SERVER_URL,DELETE_P_URL,GET_ALL_P_URL} from '../Sources/UrlsConstant'



function ViewPatients(){
    const [data,setData] = useState('')
    useEffect(()=>{
        CallToServer(SERVER_URL,"POST",GET_ALL_P_URL,data).then(async (success)=>{
           setData(await success.json())
            console.log("Response Message",data)

           },(err)=>{
            console.log("error")    
            }) 
    },[])


    return(<>
 <div className="Auth-form-container">
 <div class="col-6">
    <table class="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Id</th>
            <th scope="col">Name</th>
            <th scope="col">Disease</th>
            <th scope="col">age</th>
            <th scope="col">Operations</th>
          </tr>
        </thead>
        <tbody>
            {data.map((itm,index)=>{
                return(<tr>
                    <th>{index+1}</th>
                    <td>{itm.patId}</td>
                    <td>{itm.name}</td>
                    <td>{itm.disease}</td>
                    <td>{itm.age}</td>
                    <td>
                         <button class="btn btn-danger me-2" onClick={()=>{
                             CallToServer(SERVER_URL,"POST",DELETE_P_URL,data).then(async (success)=>{
                                setData(await success.json())
                                 console.log("Response Message",data.ResponseMsg)

                                },(err)=>{
                                 console.log("error")    
                                 }) 
                         }}>  Delete</button>
                         <button class="btn btn-warning" onClick={()=>{
                              navigate('/update/'+123)
                         }}>  Update</button>
                     </td>
                </tr>)
            })}
        </tbody>
      </table>
      </div>

      </div>
    </>)
}
export default ViewPatients
